def KmerGenerate (Dna, k):
	kmer_list=[]
	for i in range (len(Dna)-k+1):
		kmer_list.append(Dna[i:i+k])
	return kmer_list

# submit result as a txt file
if __name__ == "__main__":
	Dna=""
	k=100
	kmer_list=KmerGenerate (Dna, k)
	txt = '\n'.join(kmer_list)
	f = open('data.txt','w')
	f.write(txt)
	f.close()	
	
	
